import React, { useState, useRef } from 'react';
import { BlobServiceClient } from '@azure/storage-blob';
import axios from 'axios';
import { GoogleMap, useLoadScript, Marker } from '@react-google-maps/api';
import { useNavigate , Link} from 'react-router-dom';
//TODO: make login page the map too?
const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (event) => {
    event.preventDefault();
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const formData = new URLSearchParams();
    formData.append("username", username);
    formData.append("password", password);
    
    try {
      const response = await axios.post('http://localhost:8080/login', formData, {
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded;charset=UTF-8'
        }
      });
      //Home is temporary? eventually implement on login to load in user map
      if (response.status === 200 && !response.data.includes('Invalid')) {
        setIsLoggedIn(true);
      } else {
        console.log(response.data)
        console.error('Error logging in');
      }
    } catch (error) {
      console.error('Error logging in', error.response);
    }
  };
  if (isLoggedIn) {
    return <Map />;
  }
  function Map() {
    const { isLoaded, loadError } = useLoadScript({
      googleMapsApiKey: "AIzaSyDLLWCcjoAaSeDscglxumRkb--MAJ_jJzQ"
    });
    const mapRef = useRef();
  const onMapLoad = React.useCallback((map) => {
    mapRef.current = map;
  }, []);

  const mapContainerStyle = {
    height: "100vh",
    width: "100vw",
  };
  
  const center = {
    lat: 48.75112,
    lng: -121.25991
  };
  
  const options = {
    zoom: 7,
    mapId: "DEMO_MAP_ID",
  };

  if (loadError) return "Error loading maps";
  if (!isLoaded) return "Loading Maps";

  return (
    <div>
      <GoogleMap
        id="map"
        mapContainerStyle={mapContainerStyle}
        zoom={options.zoom}
        center={center}
        options={options}
        onLoad={onMapLoad}
      >
      </GoogleMap>
    </div>
  );
  }
  return (
    <form onSubmit={handleSubmit}>
      <label>
        Email:
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
      </label>
      <label>
        Password:
        <input
          type="text"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
      </label>
      <button type="submit">Log in</button>
      <p>
        Don't have an account? <Link to="/Register">Register</Link>
      </p>
    </form>
  );
};

export default Login;
