import React, { useState, useRef } from 'react';
//import { BlobServiceClient } from '@azure/storage-blob';
import axios from 'axios';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';
import { useNavigate , Link} from 'react-router-dom';
//TODO: make login page the map too?
const containerStyle = {
  width: '100%',
  height: '400px'
};

const center = {
  lat: 48.75112, lng: 238.74011
};

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (event) => {
    event.preventDefault();
    
    const formData = new URLSearchParams();
    formData.append("username", username);
    formData.append("password", password);
    
    try {
      const response = await axios.post('http://localhost:8080/login', formData, {
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded;charset=UTF-8'
        }
      });
      //Home is temporary? eventually implement on login to load in user map
      if (response.status === 200 && !response.data.includes('Invalid')) {
        setIsLoggedIn(true);
      } else {
        console.log(response.data)
        console.error('Error logging in');
      }
    } catch (error) {
      console.error('Error logging in', error.response);
    }
  };
  if (isLoggedIn) {
      //load markers and jaunt
  }


  



  

  return (
    <>
    <form onSubmit={handleSubmit}>
      <label>
        Email:
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
      </label>
      <label>
        Password:
        <input
          type="text"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
      </label>
      <button type="submit">Log in</button>
      <p>
        Don't have an account? <Link to="/Register">Register</Link>
      </p>
    </form>
    <LoadScript googleMapsApiKey="AIzaSyDLLWCcjoAaSeDscglxumRkb--MAJ_jJzQ">
        <GoogleMap
          mapContainerStyle={containerStyle}
          center={center}
          zoom={10}
        >
        <Marker position = {{lat: 47.609749, lng: -122.341431}} />
          
        </GoogleMap>
      </LoadScript>
    </>
    
  );
};

export default Login;
